const burger=document.querySelector(".burger"),nav=document.querySelector(".nav");burger.addEventListener("click",()=>{burger.classList.toggle("active"),nav.classList.toggle("active")});const body=document.querySelector("body"),modal=document.querySelector(".modal"),modalClose=document.querySelector(".modal-close"),signUpLink=document.querySelectorAll(".sign-up-link");signUpLink.forEach(e=>{e.addEventListener("click",()=>{body.classList.toggle("lock"),modal.classList.toggle("active")})}),modalClose.addEventListener("click",()=>{body.classList.toggle("lock"),modal.classList.toggle("active")}),$(".before-after__slider").owlCarousel({loop:!0,dots:!0,items:1});